import React, { useContext, useState } from "react";
import { format } from "date-fns";
import { EventContext } from "../context/EventContext";
import { v4 as uuidv4 } from "uuid";

const EventModal = ({ selectedDate }) => {
  const { closeModal, addEvent } = useContext(EventContext);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [time, setTime] = useState("");

  const handleSubmit = () => {
    const newEvent = {
      id: uuidv4(),
      title,
      description,
      date: format(selectedDate, "yyyy-MM-dd"),
      time
    };
    addEvent(newEvent);
    closeModal();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-10">
      <div className="bg-white p-4 rounded w-96">
        <h2 className="text-xl mb-2">Add Event - {format(selectedDate, "dd MMM yyyy")}</h2>
        <input
          type="text"
          placeholder="Event Title"
          className="border p-2 w-full mb-2"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="time"
          className="border p-2 w-full mb-2"
          value={time}
          onChange={(e) => setTime(e.target.value)}
        />
        <textarea
          placeholder="Description"
          className="border p-2 w-full mb-2"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        ></textarea>
        <div className="flex justify-end gap-2">
          <button onClick={closeModal} className="px-4 py-2 bg-gray-300 rounded">Cancel</button>
          <button onClick={handleSubmit} className="px-4 py-2 bg-blue-500 text-white rounded">Save</button>
        </div>
      </div>
    </div>
  );
};

export default EventModal;
